import React, { useState } from 'react';
import './styles/App.css';

function App() {
  const [description, setDescription] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isRecording, setIsRecording] = useState(false);

  const handleCaptureAndDescribe = async () => {
    const response = await fetch("http://localhost:5001/generate-description", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ user_input: userInput }),
    });

    if (response.ok) {
      const data = await response.json();
      setDescription(data.description);
      setUserInput("");  // Clear input after submission

      // Trigger speech output for the description
      const speakResponse = await fetch("http://localhost:5001/speak", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ text: data.description }),
      });

      if (!speakResponse.ok) {
        console.error('Error speaking the description:', speakResponse.statusText);
      }
    } else {
      console.error('Error generating description:', response.statusText);
    }
  };

  const toggleVoiceRecognition = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.onstart = () => {
      setIsRecording(true);
      console.log('Voice recognition started. Try speaking into the microphone.');
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setUserInput(transcript);  // Set the recognized text to userInput
    };

    recognition.onerror = (event) => {
      console.error('Error occurred in recognition: ' + event.error);
      setIsRecording(false);
    };

    recognition.onend = () => {
      setIsRecording(false);
      console.log('Voice recognition ended.');
    };

    if (isRecording) {
      recognition.stop();
    } else {
      recognition.start();
    }
  };

  return (
    <div className="app-container">
      <header>
        <h1 className="app-title">Vision for All</h1>
      </header>
      <main>
        <div className="input-card">
          <textarea 
            className="input-area"
            value={userInput} 
            onChange={(e) => setUserInput(e.target.value)} 
            placeholder="Ask what I see..." 
            rows={3} 
          />
          <div className="button-group">
            <button onClick={toggleVoiceRecognition} className="primary-btn">
              {isRecording ? '‚èπ Stop Recording' : 'üé§ Start Recording'}
            </button>
            <button onClick={handleCaptureAndDescribe} className="analyze-btn">
              Analyze
            </button>
          </div>
        </div>
        <section className="description-section">
          <h2>{description ? `Description: ${description}` : "No description yet."}</h2>
        </section>
      </main>
      <footer>
        <p>Designed with Accessibility in Mind</p>
      </footer>
    </div>
  );
}

export default App;
